// 保存一张base64图片，图片格式是png，图片大小是100x100。保存的路径是sdcard/test.png
var base64 = "data:image/jpeg;base64,"
// 把base64转换成图片,图片保存在sdcard/test.png
var base64Data = base64.split(",").pop() ;


// 手动解码 Base64 字符串为二进制数据
var binaryData = decodeBase64( base64Data );

// 创建 Uint8Array
var uint8Array = new Uint8Array(binaryData);

// 现在 uint8Array 包含了解码后的二进制数据

Base64.extendString();
var imgData = base64Data.toUint8Array() ;
var imgData1 = Base64.toUint8Array(base64Data) ;
var imgFile = '/test33.jpg'
var img1File = imgFile.replace(/\.jpg$/ , '-1.jpg')
console.log("image exist:" , File.exists(picturePath + imgFile) )
File.writeBytes(picturePath + imgFile , imgData );  // right
File.writeBytes(picturePath + img1File , imgData1 );  // right
